import { GithubContext } from "./githubContext";
import { githubReducer } from "./githubReducer";
import { SEARCH_USERS, GET_USER } from "../types";

export const GithubState = ({ children }) => {
  const initialState = {
    user: {},
    users: [],
    loading: false,
    repos: [],
  };
  const [state, dispatch] = useReducer(githubReducer);
  const search = async (valuse) => {
    //...

    dispatch({
      type: SEARCH_USERS,
      payload: [],
    });
  };
  const getUser = async (name) => {
    //...

    dispatch({
      type: GET_USER,
      payload: {},
    });
  };
  return <GithubContext.Provider>{children}</GithubContext.Provider>;
};
