import React, { useReduser, useReducer } from "react";
import { GithubContext } from "./githubContext";
import { githubReducer } from "./githubReducer";
import {
  SEARCH_USERS,
  GET_USER,
  GET_REPOS,
  CLEAR_USERS,
  SET_LOADING,
} from "../types";

export const GithubState = ({ children }) => {
  const initialState = {
    user: {},
    users: [],
    loading: false,
    repos: [],
  };
  console.log(process.env.REACT_APP_CLIENT_ID)
  const [state, dispatch] = useReducer(githubReducer,initialState);
  const getUsers = async (valuse) => {
    setLoading();
    //...

    dispatch({
      type: SEARCH_USERS,
      payload: [],
    });
  };
  const getUser = async (name) => {
    setLoading();
    //...

    dispatch({
      type: GET_USER,
      payload: {},
    });
  };
  const getRepos = async (name) => {
    setLoading();
    //...

    dispatch({
      type: GET_REPOS,
      payload: [],
    });
  };
  const clearUsers = () => {
    //...

    dispatch({
      type: CLEAR_USERS,
    });
  };
  const setLoading = () => {
    dispatch({
      type: SET_LOADING,
    });
  };
  const { user, users, loading, repos } = state;
  return (
    <GithubContext.Provider
      value={
        (getRepos,
        getUser,
        getUsers,
        setLoading,
        clearUsers,
        user,
        users,
        loading,
        repos)
      }
    >
      {children}
    </GithubContext.Provider>
  );
};
